clear;

p:=2^224-9035;
Fp:=GF(p);
Fp2<lambda>:=ExtensionField<Fp,x|x^2-2>;
Fp4<mu>:=ExtensionField<Fp2,x|x^2-lambda>;
Fp8<nu>:=ExtensionField<Fp4,x|x^2-mu>;

alpha:=lambda+mu+nu;
A:=EllipticCurve([Fp8|0,alpha]);
q:=p^8-p^4+1;
assert q*Random(A) eq A!0;

Fq:=GF(q);
Fq2<xi>:=ExtensionField<Fq,x|x^2+13>;

a:*xi + ;
b:*xi ;

B:=EllipticCurve([Fq2|a,b]);
N:= (p^4-1)^2*p^8;
assert N*Random(B) eq B!0;

