clear;

/////////////////
//// Fields /////
/////////////////

p:=2^256-6539;
Fp:=GF(p);
Fp2<lambda>:=ExtensionField<Fp,x|x^2-2>;
r:=8;
Fp2r<j>:=ExtensionField<Fp2,x|x^r-lambda>;

q:=p^8-p^4+1;
Fq:=GF(q);
Fq2<xi>:=ExtensionField<Fq,x|x^2+11>;

/////////////////
// Construct A //
/////////////////

alpha:=lambda+4;
E:=EllipticCurve([Fp2|0,alpha]);
assert (p^2+p+1)*Random(E) eq E!0;

Er:=BaseChange(E,Fp2r);
assert (p^8+p^4+1)*(p^8-p^4+1)*Random(Er) eq Er!0;

/////////////////
// Construct B //
/////////////////

a:*xi + ;
b:*xi ;

B:=EllipticCurve([Fq2|a,b]);
N:= (p^4-1)^2*p^8;
assert N*Random(B) eq B!0;
